SELECT     PS.dbo.PS_EMPLOYEES.EMPLID, PS.dbo.PS_N__LDAP_DATA.N__User_ID, PS.dbo.PS_EMPLOYEES.NAME, PS.dbo.PS_PERSONAL_PHONE.Phone_Cleaned, 
                      PS.dbo.PS_PERSONAL_PHONE.PHONE_TYPE, PS.dbo.PS_EMAIL_ADDRESSES.EMAIL_ADDR
FROM         PS.dbo.PS_EMPLOYEES INNER JOIN
                      PS.dbo.PS_PERSONAL_PHONE ON PS.dbo.PS_EMPLOYEES.EMPLID = PS.dbo.PS_PERSONAL_PHONE.EMPLID INNER JOIN
                      PS.dbo.PS_N__LDAP_DATA ON PS.dbo.PS_EMPLOYEES.EMPLID = PS.dbo.PS_N__LDAP_DATA.EMPLID INNER JOIN
                      PS.dbo.PS_EMAIL_ADDRESSES ON PS.dbo.PS_EMPLOYEES.EMPLID = PS.dbo.PS_EMAIL_ADDRESSES.EMPLID
GROUP BY PS.dbo.PS_EMPLOYEES.NAME, PS.dbo.PS_PERSONAL_PHONE.PHONE_TYPE, PS.dbo.PS_PERSONAL_PHONE.Phone_Cleaned, PS.dbo.PS_EMPLOYEES.EMPLID, PS.dbo.PS_N__LDAP_DATA.N__User_ID, 
                      PS.dbo.PS_EMAIL_ADDRESSES.EMAIL_ADDR